.form {
    &__group:not(:last-child){
        margin-bottom: 2rem;
    }

    &__input{
        font-size:1.5rem;
        font-family: inherit;
        color:inherit;
        padding:1.5rem 2rem;
        border-radius:2px;
        background-color: rgba($color-white, .5);
        border:none;
        border-bottom:3px solid transparent;
        width:90%;
        display:block;

        &:focus{
            outline:none;
            box-shadow: 0 1rem 2rem rgba($color-black,.1);
            border-bottom:3px solid $color-primary;
        }

        &:focus:invalid{
            border-bottom: 3px solid $color-secondary-dark;
        }

        &::-webkit-input-placeholder{
            color:$color-grey-dark-2;
        }
    }

    &__label{
        font-size:1.2rem;
        font-weight: 700;
        margin-left:2rem;
        margin-top: .7rem;
        display:block;
        transition:all .3s;
    }

    &__input:placeholder-shown + &__label{
        opacity:0;
        visibility:hidden;
        transform:translateY(-4rem);
    }

    &__radio-group{
        width:49%;
        display:inline-block;
    }

    &__radio-input{
        display:none;
    }

    &__radio-label{
        font-size:$default-font-size;
        cursor:pointer;
        position:relative ;
    }

    &__radio-button{
        height:3rem;
        width:3rem;
        border:5px solid $color-primary;
        border-radius:505;
        display:inline-block;
        position:absolute;
        left:0;
        top:-.4rem;

        &::after{
            content:"";
            display:block;
            height:1rem;
            width:1rem;
            border-radius:505;
            position:absolute;
            top:50%;
            left:50%;
            transform:translate(-50%,-50%);
            background-color:$color-primary;
            opacity:0;
            transition:opacity .2s;

        }
    }

    &__radio-input:checked ~ &__radio-label &__radio-button::after{
        opacity:1;
    }
}

